-- Change the DATABASE BACKUP PATH in Line 4 <pathname>
UPDATE DATABASE CONFIGURATION USING auto_db_backup ON auto_reorg ON auto_runstats ON auto_prof_upd ON auto_stats_prof ON;
CALL SYSPROC.AUTOMAINT_SET_POLICY ('MAINTENANCE_WINDOW', BLOB('<?xml version="1.0" encoding="UTF-8"?><DB2MaintenanceWindows xmlns="http://www.ibm.com/xmlns/prod/db2/autonomic/config"><OnlineWindow Occurrence="During" startTime="00:00:00" duration="22"><DaysOfWeek> All</DaysOfWeek><DaysOfMonth>All</DaysOfMonth><MonthsOfYear> All</MonthsOfYear></OnlineWindow><OfflineWindow Occurrence="During" startTime="23:00:00" duration="1"><DaysOfWeek> All</DaysOfWeek><DaysOfMonth>All</DaysOfMonth><MonthsOfYear> All</MonthsOfYear></OfflineWindow></DB2MaintenanceWindows> ') );
CALL SYSPROC.AUTOMAINT_SET_POLICY ( 'AUTO_BACKUP', BLOB('<?xml version="1.0" encoding="UTF-8"?><DB2AutoBackupPolicy xmlns="http://www.ibm.com/xmlns/prod/db2/autonomic/config"><BackupOptions mode="Offline"><BackupTarget><DiskBackupTarget><PathName>e:\db2backup</PathName></DiskBackupTarget></BackupTarget></BackupOptions><BackupCriteria numberOfFullBackups="1" timeSinceLastBackup="24" logSpaceConsumedSinceLastBackup="6400"/></DB2AutoBackupPolicy> ') );
CALL SYSPROC.AUTOMAINT_SET_POLICY ( 'AUTO_REORG', BLOB('<?xml version="1.0" encoding="UTF-8"?><DB2AutoReorgPolicy xmlns="http://www.ibm.com/xmlns/prod/db2/autonomic/config"><ReorgOptions dictionaryOption="Keep" indexReorgMode="Offline" useSystemTempTableSpace="false"/><ReorgTableScope ><FilterClause>TABSCHEMA NOT LIKE ''SYS%''</FilterClause></ReorgTableScope ></DB2AutoReorgPolicy> ') );
CALL SYSPROC.AUTOMAINT_SET_POLICY ( 'AUTO_RUNSTATS', BLOB('<?xml version="1.0" encoding="UTF-8"?><DB2AutoRunstatsPolicy xmlns="http://www.ibm.com/xmlns/prod/db2/autonomic/config"><RunstatsTableScope ><FilterCondition /></RunstatsTableScope ></DB2AutoRunstatsPolicy> ') );
